import React, { useState } from "react";
import useWebSocket from "react-use-websocket";
import SignalSx from "./SignalState";
import { Container, Button,TextField } from "@mui/material";


function TextForm(){
  const [text, setText] = useState("");
  const handsubmit = () =>{
    console.log("your wrote:",text);
    sendMessage(text);
  };
  return(
    <div>
       <TextField  
            fullWidth 
            id="outlined-basic" 
            label="Outlined" 
            variant="outlined"
            value={text}
            onChange={(e) => {
              setText(e.target.value);
            }}
            
            
            />
      <Button variant="text" onClick={handsubmit}>
           Send
      </Button>
       </div>
  )

}


function BasicWS() {
  const URL = "ws://127.0.0.1:8000";;

  const [isOpen, setIsOpen] = useState(false);

  const {
    sendMessage,
    sendJsonMessage,
    lastMessage,
    lastJsonMessage,
    readyState,
    getWebSockete,
  } = useWebSocket(URL, {
    onOpen: () => {
      console.log("opened");
      setIsOpen(true);
    },

    shouldReconnect: (CloseEvent) => true,
    //它用于判断当 WebSocket 连接关闭时是否应该尝试重新连接。
  });

 // const SendMsg = () => {
 //   sendMessage("Hello SendMSG");
 // };
// const sendJsonMsg = () =>{
//     sendJsonMessage()
// }

  return (
    <div>
      <Container maxWidth="sm"> 
       <span>
       <h1>Send msg into TouchDesigner</h1>
        <SignalSx color={isOpen ? 1 : 0} />
        </span>
      </Container>
     
    <div>
    <Container maxWidth="sm">
    
      <TextForm/>
      
    </Container>
      </div>
      
    </div>
  );
}

export default BasicWS;
